package com.example.recyclerviewkotlin

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.LinearLayout
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import kotlinx.android.synthetic.main.activity_main.*

class MainActivity : AppCompatActivity() {

    lateinit var wisata:WisataAdapter
    var data = arrayListOf<Wisata>()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        addData()
        wisata = WisataAdapter(data)
        var layoutManager: RecyclerView.LayoutManager = LinearLayoutManager(this)

        recyclerview.layoutManager = layoutManager
        recyclerview.adapter = wisata

    }

    fun addData(){
        data.add(Wisata("Monass","Icon Nasional","https://upload.wikimedia.org/wikipedia/commons/b/b1/Merdeka_Square_Monas_02.jpg"))
        data.add(Wisata("Borobudur","Wisata Keajaiban dunia tempat ibadah orang budha","data:image/jpeg;base64,"))

        data.add(Wisata("Raja Ampat","Wisata Laut","https://sgp1.digitaloceanspaces.com/tz-mag-id/wp-content/uploads/2018/03/010103031010/9-2-Pianemo-By-elisabeth.mita_.jpg"))
        data.add(Wisata("Big Ben","Jam icon kota London","https://media-cdn.tripadvisor.com/media/photo-s/18/b4/60/54/big-ben.jpg"))
        data.add(Wisata("Gunung Fuji","Gunung Icon Jepang","https://resources.matcha-jp.com/old_thumbnails/720x2000/581.jpeg"))
    }
}
